mapper.insert:
INSERT INTO USER2(
ID, PASSWORD, NAME
)VALUES (
#{ID}
,#{PASSWORD}
,#{NAME}
)

mapper.select:
SELECT COUNT(*)
FROM USER2
WHERE ID= #{ID}

mapper.update:
